mapper.select:
select no, subject, id, reply_group, depth, hit, date_format(writedate, '%y.%m.%d %h:%i') writedate from board_customerservice
where ${searchKey} like '%${searchWord}%'
order by reply_group desc, depth asc limit ${onePageRecord} offset ${offsetPoint}

select no,reply_group, subject, content, hit, id, writedate
from board_customerservice
where no=${param1}

select no, subject, content, id, hit, date_format(writedate, '%y년%m월%d일') writedate from board_event
where ${searchKey} like '%${searchWord}%'
order by no desc limit ${onePageRecord} offset ${offsetPoint}

select no, subject, id, reply_group, reply_seq, depth, hit, date_format(writedate, '%y.%m.%d %h:%i') writedate from board_question
where ${searchKey} like '%${searchWord}%'
order by reply_group desc, depth asc limit ${onePageRecord} offset ${offsetPoint}

select no,reply_group, subject, content, hit, id, writedate, depth
from board_question
where no=${param1}
